const slides=document.getElementById("slides"),dots=document.querySelector(".dots"),slideData=[{id:1,src:"cuba_1.jpg"},{id:2,src:"cuba_2.jpg"},{id:3,src:"cuba_3.jpg"},{id:4,src:"cuba_4.jpg"},{id:5,src:"cuba_5.jpg"},{id:6,src:"cuba_6.jpg"},{id:7,src:"cuba_7.jpg"},{id:8,src:"cuba_8.jpg"},{id:9,src:"cuba_9.jpg"},{id:10,src:"cuba_10.jpg"},{id:11,src:"cuba_11.jpg"},{id:12,src:"cuba_12.jpg"}],numSlides=slideData.length;slideData.forEach(e=>{const s=document.createElement("div");s.classList.add("my-slides","fade"),s.innerHTML=`\n    <div class="number-text">${e.id} / ${numSlides}</div>\n      <img src="./pics/${e.src}" width=100% />\n    </div>\n  `,slides.appendChild(s),dots.innerHTML+=`<span class="dot" onclick="currentSlide(${e.id})"></span>`});let slideIndex=1;function plusSlides(e){showSlides(slideIndex+=e)}function currentSlide(e){showSlides(slideIndex=e)}function showSlides(e){const s=document.getElementsByClassName("my-slides"),d=document.getElementsByClassName("dot");e>s.length&&(slideIndex=1),e<1&&(slideIndex=s.length);for(let e=0;e<s.length;e++)s[e].style.display="none";for(let e=0;e<d.length;e++)d[e].className=d[e].className.replace(" active","");s[slideIndex-1].style.display="block",d[slideIndex-1].className+=" active"}function autoTransition(){if(document.getElementById("chk").checked){const e=document.getElementsByClassName("my-slides"),s=document.getElementsByClassName("dot");for(let s=0;s<e.length;s++)e[s].style.display="none";let d=slideIndex;d>e.length&&(d=1);for(let e=0;e<s.length;e++)s[e].classList.remove("active");e[d-1].style.display="block",s[d-1].classList.add("active"),d++,slideIndex=d,setTimeout(autoTransition,8e3)}}function clicked(){const e=document.getElementById("chk");e.checked?e.checked=!1:(e.checked=!0,autoTransition())}showSlides(slideIndex),autoTransition();