let slideIndex=1;function plusSlides(e){showSlides(slideIndex+=e)}function currentSlide(e){showSlides(slideIndex=e)}function showSlides(e){const t=document.getElementsByClassName("my-slides"),s=document.getElementsByClassName("dot");e>t.length&&(slideIndex=1),e<1&&(slideIndex=t.length);for(let e=0;e<t.length;e++)t[e].style.display="none";for(let e=0;e<s.length;e++)s[e].className=s[e].className.replace(" active","");t[slideIndex-1].style.display="block",s[slideIndex-1].className+=" active"}function autoTransition(){if(document.getElementById("chk").checked){const e=document.getElementsByClassName("my-slides"),t=document.getElementsByClassName("dot");for(let t=0;t<e.length;t++)e[t].style.display="none";let s=slideIndex;s>e.length&&(s=1);for(let e=0;e<t.length;e++)t[e].classList.remove("active");e[s-1].style.display="block",t[s-1].classList.add("active"),slideIndex=++s,setTimeout(autoTransition,1e4)}}function clicked(){document.getElementById("chk").checked?document.getElementById("chk").checked=!1:(document.getElementById("chk").checked=!0,autoTransition())}showSlides(slideIndex),autoTransition();