const slides=document.getElementById("slides"),dots=document.querySelector(".dots"),slideData=[{id:1,src:"cuba_1.jpg"},{id:2,src:"cuba_2.jpg"},{id:3,src:"cuba_3.jpg"},{id:4,src:"cuba_4.jpg"},{id:5,src:"cuba_5.jpg"},{id:6,src:"cuba_6.jpg"},{id:7,src:"cuba_7.jpg"},{id:8,src:"cuba_8.jpg"},{id:9,src:"cuba_9.jpg"},{id:10,src:"cuba_10.jpg"},{id:11,src:"cuba_11.jpg"},{id:12,src:"cuba_12.jpg"}],numSlides=slideData.length;function getSlideId(){const e=document.querySelector(".active");return Number(e.id.replace(/\D+/,""))}function arrowNavigation(e){showSlides(getSlideId()+e)}function showSlides(e){let s=e%numSlides;0===s&&(s=numSlides);for(let e=0;e<numSlides;e++)slides.children[e].style.display="none",dots.children[e].classList.remove("active");slides.children[s-1].style.display="block",dots.children[s-1].classList.add("active")}slideData.forEach(e=>{const s=document.createElement("div");s.classList.add("my-slides","fade"),s.innerHTML=`\n  <div class="number-text">${e.id} / ${numSlides}</div>\n  <img src="./pics/${e.src}" width=100% />\n  </div>\n  `,slides.appendChild(s),dots.innerHTML+=`<span id="dot_${e.id}" class="dot" onclick="showSlides(${e.id})"></span>`}),showSlides(1);const box=document.getElementById("chk");function autoTransition(){if(box.checked){showSlides(getSlideId()+1)}}const playPause=document.getElementById("play-pause"),clicked=()=>{playPause.innerHTML=box.checked?'<i class="fa fa-play"></i>':'<i class="fa fa-pause"></i>',box.checked=!box.checked};setInterval(autoTransition,8e3);